mapper.select:
SELECT bcv.*
FROM boot_config_value bcv
       LEFT JOIN boot_config_item bci on bcv.item_id = bci.id
       LEFT JOIN boot_config_scope bcs on bcs.id = bci.scope_id


    AND bcs.scope = #{scope}


    AND bcv.ref_id = #{refId}

SELECT bcv.*
FROM boot_config_value bcv
       LEFT JOIN boot_config_item bci on bcv.item_id = bci.id
       LEFT JOIN boot_config_scope bcs on bcs.id = bci.scope_id


    AND bcs.scope = #{scope}


    AND bcv.ref_id = #{refId}


    AND bci.name = #{name}


LIMIT 1
